let trailingStopLoss = 3 // Set the percentage you wish your Trailing Stop Loss to trail

if ( chart.at01min.candle.close * ( 1 - (trailingStopLoss / 100) ) > tradingEngine.tradingCurrent.position.stopLoss.value ) {
chart.at01min.candle.close * ( 1 - (trailingStopLoss / 100) )
}
else {
tradingEngine.tradingCurrent.position.stopLoss.value
}

let trailingStopLoss = 3 // Set the percentage you wish your Trailing Stop Loss to trail

if ( chart.at01min.candle.close * ( 1 + (trailingStopLoss / 100) ) < tradingEngine.tradingCurrent.position.stopLoss.value ) {
chart.at01min.candle.close * ( 1 + (trailingStopLoss / 100) )
}
else {
tradingEngine.tradingCurrent.position.stopLoss.value
}
