riskPercentage = 1 // Initial Stop Loss is set by this Risk Percentage out of your Total Quoted Balance

profitTarget1 = 0.5 // First Percent Profit
profitDivider = 2 // The profit will be divided with this value and set your Stop Loss to the Entry Rate + Half Profit

profitTarget2 = 5 // Second Percent Profit
trailingStopLoss = 2.5 // Fixed Trailing Percentage

if ( chart.at01min.candle.close > (tradingEngine.tradingCurrent.position.entryTargetRate.value * (1 + (profitTarget2 / 100))) && tradingEngine.tradingCurrent.position.stopLoss.value < (chart.at01min.candle.close * (1 - (trailingStopLoss2 / 100))) ) {
    chart.at01min.candle.close * (1 - (trailingStopLoss / 100))
}

else if ( chart.at01min.candle.close > (tradingEngine.tradingCurrent.position.entryTargetRate.value * (1 + (profitTarget1 / 100))) && tradingEngine.tradingCurrent.position.stopLoss.value < chart.at01min.candle.close - ( (chart.at01min.candle.close - tradingEngine.tradingCurrent.position.entryTargetRate.value) / profitDivider) ) {
    chart.at01min.candle.close - ( (chart.at01min.candle.close - tradingEngine.tradingCurrent.position.entryTargetRate.value) / profitDivider)
}

else {
    tradingEngine.tradingCurrent.position.entryTargetRate.value * (1 - (( tradingEngine.tradingCurrent.tradingEpisode.episodeQuotedAsset.balance.value * (riskPercentage / 100)) / tradingEngine.tradingCurrent.strategyOpenStage.stageQuotedAsset.sizeFilled.value))
}

riskPercentage = 1 // Initial Stop Loss is set by this Risk Percentage out of your Total Quoted Balance

profitTarget1 = 0.5 // First Percent Profit
profitDivider = 2 // The profit will be divided with this value and set your Stop Loss to the Entry Rate + Half Profit

profitTarget2 = 5 // Second Percent Profit
trailingStopLoss = 2.5 // Fixed Trailing Percentage

if ( chart.at01min.candle.close < (tradingEngine.tradingCurrent.position.entryTargetRate.value * (1 - (profitTarget2 / 100))) && tradingEngine.tradingCurrent.position.stopLoss.value > (chart.at01min.candle.close * (1 + (trailingStopLoss2 / 100))) ) {
    chart.at01min.candle.close * (1 + (trailingStopLoss / 100))
}

else if ( chart.at01min.candle.close < (tradingEngine.tradingCurrent.position.entryTargetRate.value * (1 - (profitTarget1 / 100))) && tradingEngine.tradingCurrent.position.stopLoss.value > chart.at01min.candle.close + ( (chart.at01min.candle.close - tradingEngine.tradingCurrent.position.entryTargetRate.value) / profitDivider) ) {
    chart.at01min.candle.close + ( (chart.at01min.candle.close + tradingEngine.tradingCurrent.position.entryTargetRate.value) / profitDivider)
}

else {
    tradingEngine.tradingCurrent.position.entryTargetRate.value * (1 + (( tradingEngine.tradingCurrent.tradingEpisode.episodeQuotedAsset.balance.value * (riskPercentage / 100)) / tradingEngine.tradingCurrent.strategyOpenStage.stageQuotedAsset.sizeFilled.value))
}
